import React , {useState} from "react";
import { Link } from "react-router-dom";
import classnames from "classnames";
import { InputGroup , Button , Form , Card , Container } from "react-bootstrap";


const CreateSchema = (props) => {

    const [ email , setEmail ] = useState("");
    const [ password , setPassword ] = useState("");
    const [ errors , setErrors ] = useState({});

    const onSubmit = () => {
    };
    return (<>
        <Container className="d-flex flex-column align-items-center">
        <Card className="shadow w-75 mt-5">
            <Card.Header className='pl-5 pt-3 pb-2 d-flex flex-column align-items-center bg-dark text-white'>
                <strong>Create Schema</strong>
            </Card.Header>
            <Card.Body className='px-5'>
                <Form onSubmit={onSubmit}>
                    <Form.Group className="mt-2 mb-3">
                        <Form.Control
                            type="email" 
                            id="email"
                            placeholder="Email Address" 
                            value={email} 
                            error={errors}
                            onChange={e => setEmail(e.target.value)}
                            isInvalid={!!errors.email || !!errors.emailnotfound}
                        />
                        <Form.Control.Feedback type='invalid'>
                            {errors.email}
                            {errors.emailnotfound}
                        </Form.Control.Feedback>
                    </Form.Group>

                    <div className='d-flex flex-column align-items-center mt-3'>
                        <Button variant="primary" type="submit">
                            Submit
                        </Button>
                    </div>
                </Form>
            </Card.Body>
        </Card>
        </Container>
    </>);
}

export default CreateSchema;
