import React, { Component } from "react";
import { Link } from "react-router-dom";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import { loginUser } from "../../actions/authActions";
import classnames from "classnames";
import { InputGroup , Button , Form , Card } from "react-bootstrap";

class Login extends Component{
    constructor(){
        super()
        this.state = {
            email:"",
            password:"",
            errors:{}
        }
    }
    componentDidMount() {
        if (this.props.auth.isAuthenticated) {
          this.props.history.push("/dashboard");
        }
      }

    componentWillReceiveProps(nextProps) {
        if (nextProps.auth.isAuthenticated) {
          this.props.history.push("/dashboard"); 
        }

    if (nextProps.errors) {
          this.setState({
            errors: nextProps.errors
          });
        }
      }

    onChange = e => {
        this.setState({[e.target.id]:e.target.value})
    }

    onSubmit = e => {
        e.preventDefault();

        const userData = {
            email:this.state.email,
            password:this.state.password,
        }
        this.props.loginUser(userData);
    }
    render(){
        const {email, password, errors} = this.state;
        return(<>
            <div className="form-box">
            <form className="login-form" onSubmit={this.onSubmit}>
                <h2>Login</h2>
                <hr/>
                <div className="form-group">
                    <input type="email" 
                           id="email"
                           placeholder="Email Address" 
                           value={email} 
                           error={errors}
                           onChange={this.onChange}
                           className={classnames("form-control", {
                            invalid: errors.email || errors.emailnotfound
                          })}/>
                          <span className="red-text">
                            {errors.email}
                            {errors.emailnotfound}
                        </span>
                </div>
                <div className="form-group">
                    <input type="password"  
                           id="password" 
                           placeholder="Password" 
                           value={password} 
                           error={errors}
                           onChange={this.onChange}
                           className={classnames("form-control", {
                            invalid: errors.password || errors.passwordincorrect })}
                            />
                    <span className="red-text">
                        {errors.password}
                        {errors.passwordincorrect}
                    </span>
                </div>
                <div className="form-group">
                    <button type="submit" className="btn btn-primary btn-block btn-lg">Login</button>
                </div>
                <div className="text-center">Don't have an account? <Link to="/register">Register</Link></div>


            </form>
        </div>
        <Container className="d-flex flex-column align-items-center">
        <Card className="shadow w-50 mt-5">
            <Card.Header className='pt-3 pb d-flex flex-column align-items-center bg-dark text-white'>
                <h2><strong>Create ITO</strong></h2>
            </Card.Header>
            <Card.Body className='px-5'>
                <Form>
                    <Form.Group className="mb-3">
                        <Form.Label><strong>Number of Shares</strong></Form.Label>
                        <Form.Control
                            type="number"
                            placeholder="Enter the Number of Shares"
                            value={shareCount}
                            onChange={(event) => {setShareCount(parseInt(event.target.value))}}
                            isInvalid={!!formErrors.shareCount}
                        />
                        <Form.Control.Feedback type='invalid'>
                            { formErrors.shareCount }
                        </Form.Control.Feedback>
                    </Form.Group>

                    <Form.Group className="mb-3">
                        <Form.Label><strong>Price per share</strong></Form.Label>
                        <InputGroup>
                            <Form.Control
                                type="number"
                                placeholder="Enter Price per share"
                                value={sharePrice}
                                onChange={(event) => {setSharePrice(parseInt(event.target.value))}}
                                isInvalid={!!formErrors.sharePrice}
                            />
                            <InputGroup.Text id="basic-addon2">Youth Tokens</InputGroup.Text>
                            <Form.Control.Feedback type='invalid'>
                                { formErrors.sharePrice }
                            </Form.Control.Feedback>
                        </InputGroup>
                    </Form.Group>
                    <div className='d-flex flex-column align-items-center'>
                    <Button variant="primary" type="submit">
                        Submit
                    </Button>
                    </div>
                </Form>
            </Card.Body>
        </Card>
        </Container>
        </>)
    }
}

Login.propTypes = {
    loginUser: PropTypes.func.isRequired,
    auth: PropTypes.object.isRequired,
    errors: PropTypes.object.isRequired
  };

  const mapStateToProps = state => ({
    auth: state.auth,
    errors: state.errors
  });

  export default connect(
    mapStateToProps,
    { loginUser }
  )(Login);
