import React , {useState} from "react";
import { Link } from "react-router-dom";
import classnames from "classnames";
import { Row , Col , FormControl , InputGroup , Button , Form , Card , Container } from "react-bootstrap";


const CreateSchema = () => {

    const [ form , setForm ] = useState([]);

    const addProperty = (e) => {
        e.preventDefault();
        setForm( (prev) => {
            return [
                ...prev,
                {
                    propertyName : "",
                }
            ]
        })
        console.log(form);
    }

    const onPropertyChange = (e,index) => {
        e.preventDefault();
        setForm((prev) => {
            prev?.map( (value,ind) => {
                return value;
                /*
                if(ind!==index){
                    console.log(value);
                }
                let val =  {
                    ...value,
                    [e.target.name] : e.target.value,
                }
                console.log(ind);
                return val;
                */
            })
        })
        console.log("done prevent change")
    }

    const onSubmit = (e) => {
        e.preventDefault();
        console.log(form);
    };

    return (<>
        <Container className="d-flex flex-column align-items-center">
        <Card className="shadow w-75 mt-5">
            <Card.Header className='pl-5 pt-3 pb-2 d-flex flex-column align-items-center bg-dark text-white'>
                <h2><strong>Create Schema</strong></h2>
            </Card.Header>
            <Card.Body className='px-5'>
                <Form onSubmit={onSubmit}>

                    <Form.Group as={Row} className="mb-3">
                        <Form.Label column sm="2">
                            Name
                        </Form.Label>
                        <Col sm="10">
                            <Form.Control type="text"/>
                        </Col>
                    </Form.Group>
                    <Form.Group as={Row} className="mb-4">
                        <Form.Label column sm="2">
                            Description
                        </Form.Label>
                        <Col sm="10">
                            <FormControl as="textarea"/>
                        </Col>
                    </Form.Group>

                    <Form onSubmit={addProperty}>
                        {
                            form.map( (value,index) => {
                                return (
                                <Form.Group as={Row} className="mb-3">
                                    <hr />
                                    <Form.Label column sm="2">
                                        Name
                                    </Form.Label>
                                    <Col sm="10">
                                        <Form.Control
                                            type="text"
                                            //value={value.propertyName}
                                            name="propertyName"
                                            onChange={(e) => onPropertyChange(e,index)}
                                        />
                                    </Col>
                                </Form.Group>);
                            })
                        }
                        <hr />
                        <div className='d-flex flex-column align-items-end mt-3'>
                            <Button variant="success" type="submit">
                                Add Property
                            </Button>
                        </div>
                    </Form>
                    <div className='d-flex flex-column align-items-center mt-3'>
                        <Button variant="primary" type="submit">
                            Submit
                        </Button>
                    </div>
                </Form>
            </Card.Body>
        </Card>
        </Container>
    </>);
}

export default CreateSchema;
